import { useState } from "react";

// import styles of this component
import styles from "./App.module.css"

// import other components to use
import Header from './Components/Header/Header';
import MasonryLayout from './Components/MasonryLayout/MasonryLayout';
import ContainerCard from './Components/ContainerCard/ContainerCard';
import Dropdown from './Components/Elements/Dropdown/Dropdown';

// import json files 
import images from "./Jsons/Images.json"
import { AuthProvider } from "react-oidc-context";
import { BrowserRouter, Route, Router, Routes } from "react-router-dom";
import Index from "./Layouts/Index";


// App component
const App = () => {
    // dropdown items
    const ddItems = [
        {
            id: 1,
            title: "All Images",
            active: true
        },
        {
            id: 2,
            title: "Topic Images",
            active: false
        },
        {
            id: 3,
            title: "Nature Images",
            active: false
        },
        {
            id: 4,
            title: "NFT Images",
            active: false
        },
        {
            id: 5,
            title: "Space Images",
            active: false
        }
    ]

    const oidcConfig = {
        authority: "http://keycloak:8080/auth/realms/my_realm/",
        client_id: "frontend_client",
        redirect_uri: "http://localhost:3000/signin-callback.html",
        response_type: 'code',
        scope: "openid profile message.read",
    };


    const [categoryImage, setCategoryImage] = useState(images.categories.all)

    const takeDdTitle = (ddTitle) => {
        setCategoryImage(() => {
            let categoryChoose = Object.keys(images.categories).filter(item => {
                const titleSplited = ddTitle.toLowerCase().split(" ")[0]
                return item.toLowerCase().includes(titleSplited)
            })
            return [...images.categories[categoryChoose]]
        })
    }

    return (
        <AuthProvider {...oidcConfig}>
            <BrowserRouter>
                <Routes>
                    <Route path="/" element={<Header />} />
                    <Route path="/explore" element={<Index />} />
                </Routes>
            </BrowserRouter>
        </AuthProvider>
    )
}

export default App
