import React from "react";
// import styles of this component
import styles from "./Header.module.css"

// import other components
import ContainerCard from '../ContainerCard/ContainerCard';
import Nav from "../Nav/Nav"

// import something from react packages
import { AddCircle, DocumentUpload, Image, SearchNormal1 } from "iconsax-react";
import { Setting4 } from "iconsax-react";

import { useAuth } from "react-oidc-context";

// Header component
const Header = () => {
    const auth = useAuth();

    const hiddenFileInput = React.useRef(null);

    // Programatically click the hidden file input element
    // when the Button component is clicked
    const handleFileClick = event => {
        hiddenFileInput.current.click();
    };

    return (
        <header className={`${styles.header} flex justify-content-center`}>
            <ContainerCard className="flex flex-column">
                <div className={styles["blur-circle-shape"]}></div>

                <Nav />
                {
                    //*<BrickLayout />
                }

                <div className={`${styles["headings-header"]} flex justify-content-center flex-column `}>
                    {auth.isAuthenticated &&
                        <h2 className={styles["heading-header-title"]}>Hello {auth.user?.profile.name}ðŸ˜ŽðŸ¤˜</h2>
                    }
                    <h1 className={styles["heading-header-second-title"]}>
                        Artists make The Arts better <br />
                        The Arts design the <span>world</span> better
                    </h1>

                    <div className={`${styles["search-bar"]} flex align-items-center`}>
                        <SearchNormal1 size="30" color="var(--white-100)" />
                        <input type="text" className={styles["search-input"]} placeholder="Search who can change the world" />
                        <button className={`${styles["search-btn"]} flex justify-content-center align-items-center`}>
                            <Setting4 size="20" color="var(--dark-900)" />
                        </button>
                    </div>
                    <form >

                        <div className={`${styles["search-bar"]} flex align-items-center`} htmlFor="img">
                            <Image size="30" color="var(--white-100)" />

                            <span className={`${styles["inputTextSpan"]}`}>Select your maginificent image</span>
                            <input ref={hiddenFileInput} type="file" className={styles["search-input"]} placeholder="Upload a file" id="img" style={{ opacity: "0" }} />
                            <button onClick={handleFileClick} className={`${styles["search-btn"]} flex justify-content-center align-items-center`}>
                                <DocumentUpload size="20" color="var(--dark-900)" />
                            </button>

                        </div>
                        <label htmlFor="file-upload">Choose a file:</label>
                        <button type="submit">Submit</button>
                    </form>

                    {
                        //*<HeaderBoxes titles_numbers={JsonHeader.informations} /> 
                    }
                </div>
            </ContainerCard>
        </header>
    )
}

export default Header
